svg.script:
// 

var svgNS = "http://www.w3.org/2000/svg";
var xlinkNS = "http://www.w3.org/1999/xlink";
var polygon, points_group;

function setup() {
   polygon = document.getElementById("polygon");
   points_group = document.getElementById("points");

   var vertex_list = polygon.points;
   var i=0;
   while (i < vertex_list.numberOfItems) {
       var newusetag = document.createElementNS(svgNS, "use");
       newusetag.setAttributeNS(xlinkNS, "xlink:href", "#vertex");
       var vertex = vertex_list.getItem(i);
       newusetag.setAttributeNS(null, "x", ""+vertex.x);
       newusetag.setAttributeNS(null, "y", ""+vertex.y);
       newusetag.setAttributeNS(null, "id", "v"+i);
       points_group.appendChild(newusetag);
       i=i+1;
   }
}

//
